				}
			}
		}
		$this->lastArcG2tCriteria = $criteria;
		return $this->collArcG2ts;
	}

	/**
	 * Returns the number of related ArcG2t objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related ArcG2t objects.
	 * @throws     PropelException
	 */
	public function countArcG2ts(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(ArcTriplePeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collArcG2ts === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(ArcG2tPeer::T, $this->t);

				$count = ArcG2tPeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(ArcG2tPeer::T, $this->t);

				if (!isset($this->lastArcG2tCriteria) || !$this->lastArcG2tCriteria->equals($criteria)) {
					$count = ArcG2tPeer::doCount($criteria, $con);
				} else {
					$count = count($this->collArcG2ts);
				}
			} else {
				$count = count($this->collArcG2ts);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a ArcG2t object to this object
	 * through the ArcG2t foreign key attribute.
	 *
	 * @param      ArcG2t $l ArcG2t
	 * @return     void
	 * @throws     PropelException
	 */
	public function addArcG2t(ArcG2t $l)
	{
		if ($this->collArcG2ts === null) {
			$this->initArcG2ts();
		}
		if (!in_array($l, $this->collArcG2ts, true)) { // only add it if the **same** object is not already associated
			array_push($this->collArcG2ts, $l);
			$l->setArcTriple($this);
		}
	}


	/**
