      }
    }


		if ($con === null) {
			$con = Propel::getConnection(SchemaPropertyPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
