		if ($this->isColumnModified(sfBlogLogPeer::VERB)) $criteria->add(sfBlogLogPeer::VERB, $this->verb);
		if ($this->isColumnModified(sfBlogLogPeer::OBJECT_CLASS)) $criteria->add(sfBlogLogPeer::OBJECT_CLASS, $this->object_class);
		if ($this->isColumnModified(sfBlogLogPeer::OBJECT_ID)) $criteria->add(sfBlogLogPeer::OBJECT_ID, $this->object_id);
