	/**
	 * Method perform an INSERT on the database, given a sfBlogPost or Criteria object.
	 *
	 * @param      mixed $values Criteria or sfBlogPost object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BasesfBlogPostPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BasesfBlogPostPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(sfBlogPostPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from sfBlogPost object
		}

		if ($criteria->containsKey(sfBlogPostPeer::ID) && $criteria->keyContainsValue(sfBlogPostPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.sfBlogPostPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		
    foreach (sfMixer::getCallables('BasesfBlogPostPeer:doInsert:post') as $callable)
    {
      call_user_func($callable, 'BasesfBlogPostPeer', $values, $con, $pk);
    }

    return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a sfBlogPost or Criteria object.
	 *
	 * @param      mixed $values Criteria or sfBlogPost object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BasesfBlogPostPeer:doUpdate:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BasesfBlogPostPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(sfBlogPostPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(sfBlogPostPeer::ID);
			$selectCriteria->add(sfBlogPostPeer::ID, $criteria->remove(sfBlogPostPeer::ID), $comparison);

		} else { // $values is sfBlogPost object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);
	

    foreach (sfMixer::getCallables('BasesfBlogPostPeer:doUpdate:post') as $callable)
    {
      call_user_func($callable, 'BasesfBlogPostPeer', $values, $con, $ret);
    }

    return $ret;
  }

