	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(SfWebpanelBugsPeer::DATABASE_NAME);

		if ($this->isColumnModified(SfWebpanelBugsPeer::ID)) $criteria->add(SfWebpanelBugsPeer::ID, $this->id);
		if ($this->isColumnModified(SfWebpanelBugsPeer::TITLE)) $criteria->add(SfWebpanelBugsPeer::TITLE, $this->title);
		if ($this->isColumnModified(SfWebpanelBugsPeer::MODULE_NAME)) $criteria->add(SfWebpanelBugsPeer::MODULE_NAME, $this->module_name);
		if ($this->isColumnModified(SfWebpanelBugsPeer::ACTION_NAME)) $criteria->add(SfWebpanelBugsPeer::ACTION_NAME, $this->action_name);
		if ($this->isColumnModified(SfWebpanelBugsPeer::APP_NAME)) $criteria->add(SfWebpanelBugsPeer::APP_NAME, $this->app_name);
		if ($this->isColumnModified(SfWebpanelBugsPeer::DATE_ADDED)) $criteria->add(SfWebpanelBugsPeer::DATE_ADDED, $this->date_added);
		if ($this->isColumnModified(SfWebpanelBugsPeer::DESCRIPTION)) $criteria->add(SfWebpanelBugsPeer::DESCRIPTION, $this->description);
		if ($this->isColumnModified(SfWebpanelBugsPeer::URL)) $criteria->add(SfWebpanelBugsPeer::URL, $this->url);
		if ($this->isColumnModified(SfWebpanelBugsPeer::SOLVED)) $criteria->add(SfWebpanelBugsPeer::SOLVED, $this->solved);

		return $criteria;
	}

